function bc_spotlight(){function getCSSVariableValue(variable){if(variable.startsWith('var(')&&variable.endsWith(')')){const variableName=variable.slice(4,-1).trim();return getComputedStyle(document.documentElement).getPropertyValue(variableName).trim()}
return variable}
document.querySelectorAll(".bc-spotlight").forEach(function(e){e.removeAttribute('data-flickering')
var g=e.getAttribute("data-gradient-colors").split("bc11").filter(Boolean);var palette=e.getAttribute("data-palette");if(palette=='custom'){g=g.map(color=>getCSSVariableValue(color));if(g.length<2){g=g.concat(g)}
let gradientStops='';const totalColors=g.length;const stepSize=20/totalColors;g.forEach((color,index)=>{const startPercentage=10+(index*stepSize);const endPercentage=10+((index+1)*stepSize);gradientStops+=`${color} ${startPercentage.toFixed(2)}%, ${color} ${endPercentage.toFixed(2)}%${index < totalColors - 1 ? ',' : ''}`});const rainbowGradient=`repeating-linear-gradient(100deg, ${gradientStops})`;e.style.setProperty('--rainbow',rainbowGradient)}})}
document.addEventListener("DOMContentLoaded",function(t){if(typeof bricksIsFrontend!=='undefined'&&bricksIsFrontend){bc_spotlight()}})