function next_arrow_button_v7() {
    document.querySelectorAll('.nb-arrow-button-v7').forEach(button => {
        button.removeAttribute('data-flickering');
        var textContainer = button.querySelector('.nb-arrow-button-v7__text-container');
        var textElement = button.querySelector('.nb-arrow-button-v7__text');
        var textSwapElement = button.querySelector('.nb-arrow-button-v7__text-swap');
        
        if (textElement && textSwapElement) {
            var textWidth = textElement.offsetWidth;
            var swapWidth = textSwapElement.offsetWidth;
            var textHeight = textElement.offsetHeight;
            var swapHeight = textSwapElement.offsetHeight;

            textContainer.style.width = Math.max(textWidth, swapWidth) + 'px';
            textContainer.style.height = Math.max(textHeight, swapHeight) + 'px';
            
            textElement.style.position = 'absolute';
            textSwapElement.style.position = 'absolute';
        }
    });
}
document.addEventListener("DOMContentLoaded", function (t) {
    bricksIsFrontend && next_arrow_button_v7();
});