function next_image_reveal_v2(){var isBricks=window.location.href.includes("brickspreview");document.querySelectorAll('.nb-image-reveal-v2').forEach(imageReveal=>{imageReveal.removeAttribute('data-flickering');if(isBricks&&imageReveal.getAttribute('data-disable-builder').includes('1')){imageReveal.querySelector('.nb-image-reveal-v2__mask').style.transform='unset';imageReveal.querySelector('.nb-image-reveal-v2__image').style.transform='unset';imageReveal.querySelector('.nb-image-reveal-v2__image').style.position='relative';return}
var animationType=imageReveal.getAttribute("data-animationType");var removescroll=imageReveal.getAttribute("data-remove-scroll");var classTriggerClick=imageReveal.getAttribute("data-classTriggerClick");var classTriggerHover=imageReveal.getAttribute("data-classTriggerHover");var varTrigger=imageReveal.getAttribute("data-trigger");if(varTrigger=='this'){varTrigger=imageReveal}
var varContainerAnimation=isInsideHorizontalScrollElement(imageReveal);const tl=gsap.timeline({scrollTrigger:{trigger:varTrigger,start:imageReveal.getAttribute("data-start"),end:imageReveal.getAttribute("data-end"),scrub:imageReveal.getAttribute("data-scrub")==="1"?!0:imageReveal.getAttribute("data-scrub")==="0"?!1:!1,toggleActions:imageReveal.getAttribute("data-toggle-actions"),containerAnimation:varContainerAnimation?window[varContainerAnimation]:undefined}});tl.to(".nb-image-reveal-v2__mask",{transform:"translateX(0) translateY(0)",duration:parseFloat(imageReveal.getAttribute("data-mask-duration")),ease:imageReveal.getAttribute("data-mask-ease"),delay:parseFloat(imageReveal.getAttribute("data-mask-delay")),});tl.to(".nb-image-reveal-v2__image",{transform:"translateX(0) translateY(0)",duration:parseFloat(imageReveal.getAttribute("data-image-duration")),ease:imageReveal.getAttribute("data-image-ease"),},parseFloat(imageReveal.getAttribute("data-image-delay")));var y=imageReveal.closest(".bc-core-menu");if(y&&y.contains(imageReveal)){var v=y.querySelector(".bc-core-burguer").querySelector("button");tl.pause(!0);new MutationObserver(function(e){for(var t of e)"attributes"===t.type&&("true"===v.getAttribute("aria-expanded")?f():A())}).observe(v,{attributes:!0})}
function f(){tl.isActive()?tl.play():tl.restart()}
function A(){tl.reverse()}
if("Click"==animationType&&0!=parseFloat(classTriggerClick)){document.querySelectorAll(classTriggerClick).forEach(function(t){t.addEventListener("click",function(){tl.restart()})})}
if("Hover"==animationType&&0!=parseFloat(classTriggerHover)){document.querySelectorAll(classTriggerHover).forEach(function(t){t.addEventListener("mouseover",function(){tl.restart()})})}
if(removescroll.includes('1')){tl.progress(1)}})}
function isInsideHorizontalScrollElement(element){let containerName=null;document.querySelectorAll(".next-horizontal-scroll").forEach((horizontalScroll,index)=>{if(horizontalScroll.contains(element)){containerName=`NextHorizontalScroll${index}`}});return containerName}
document.addEventListener("DOMContentLoaded",function(t){if(!document.querySelector(".nb-site-loader")){bricksIsFrontend&&next_image_reveal_v2()}else{document.querySelectorAll(".nb-image-reveal-v2").forEach((r)=>{if(r.getAttribute("data-remove-scroll")=="1"||isInsideHorizontalScrollElement(r)||r.closest(".bc-core-menu")){r.removeAttribute('data-flickering');r.querySelector('.nb-image-reveal-v2__mask').style.transform='unset';r.querySelector('.nb-image-reveal-v2__image').style.transform='unset';r.querySelector('.nb-image-reveal-v2__image').style.position='relative'}})}})