function next_nudge_button(){document.querySelectorAll('.nb-nudge-button').forEach(button=>{var buttonHeight=button.offsetHeight;var nudge=button.querySelector('.nb-nudge-button__nudge');var computedStyle=window.getComputedStyle(nudge);var nudgeHeight=parseFloat(computedStyle.getPropertyValue('--nudge-height'));var paddingVertical=parseFloat(computedStyle.getPropertyValue('--padding-vertical'));if(nudgeHeight>buttonHeight-(paddingVertical*2)){button.style.setProperty('--nudge-height',`${buttonHeight - (paddingVertical * 2)}px`)}})}
document.addEventListener("DOMContentLoaded",function(t){bricksIsFrontend&&next_nudge_button()})